import React, { Component, useEffect } from "react";
import AuctionContract from "./contracts/Auction.json";
import getWeb3 from "./getWeb3";

import "./App.css";


class App extends Component {
    state = { highestBid: 0, web3: null, accounts: null, contract: null, owner: null, highestBidder: null, tempValue: 0};

    componentDidMount = async () => {
        try {
        // Get network provider and web3 instance.
        const web3 = await getWeb3();

        // Use web3 to get the user's accounts.
        const accounts = await web3.eth.getAccounts();

        // Get the contract instance.
        const networkId = await web3.eth.net.getId();
        const deployedNetwork = AuctionContract.networks[networkId];
        const instance = new web3.eth.Contract(
            AuctionContract.abi,
            deployedNetwork && deployedNetwork.address,
        );

        const highestBidInit = instance.methods.highestBid.call();
        const highestBidderInit = instance.methods.highestBidder.call();
        const ownerInit = instance.methods.owner.call();
        await this.setState({highestBid: highestBidInit});
        await this.setState({highestBidder: highestBidderInit});

        // Set web3, accounts, and contract to the state, and then proceed with an
        // example of interacting with the contract's methods.
        this.setState({ web3, accounts, contract: instance }, this.OwnerMethod);
        } catch (error) {
        // Catch any errors for any of the above operations.
        alert(
            `Failed to load web3, accounts, or contract. Check console for details.`,
        );
        console.error(error);
        }
    };


    OwnerMethod = async () => {
        const { accounts, owner } = this.state;
        this.setState({ owner: accounts });
    };

    submitBid = (event) => {
        const {highestBid, tempValue, contract, accounts} = this.state;
        if(highestBid < tempValue){
            this.setState({highestBid: Number(tempValue)});
        }
        contract.methods.bid().send({from: accounts[0]});
        event.preventDefault();
    }

    BidHandler = (event) => {
        this.setState({tempValue: event.target.value});
    }

    submitWithdraw = (event) => {
        alert("asd");
    }

    render() {
        if (!this.state.web3) {
        return <div>Loading Web3, accounts, and contract...</div>;
        }
        return (
            <div className="App">
                <h1>Owner: {this.state.owner}</h1>
                <h2>Highest bid: </h2>
                <h3>Highest bidder address: </h3>
                <h4>Contract value: </h4>
                <div className="Bid">
                    <form onSubmit={this.submitBid}>
                        <input
                            type='text'
                            onChange={this.BidHandler}
                        />
                        <input type='submit'/>
                    </form>
                </div>
                <div className="withdraw">
                    <form onSubmit={this.submitWithdraw}>
                        <input type='submit'/>
                    </form>
                </div>
            </div>
        );
    }
}

export default App;
